export class FaceSnap {
    title: string;
    description: string;
    createdDate: Date;
    snaps: number;
    imageUrl: string;
    isSnapped!:boolean;
    
    constructor(title: string, description: string, imageUrl: string, createdDate: Date, snaps: number, isSnapped : boolean) {
      this.title = title;
      this.description = description;
      this.imageUrl = imageUrl;
      this.createdDate = createdDate;
      this.snaps = snaps;
      this.isSnapped = isSnapped;
    }
    
    onAddSnap() {
        if (this.isSnapped===false){
           this.snaps++;
           this.isSnapped=true;
        }else{
          this.snaps--;
          this.isSnapped=false;
        }  } 
  }
