import { Component, OnInit, Input } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})


export class FaceSnapComponent implements OnInit {

  title!: string;
  description!: string;
  createdDate!: Date;
  snaps!: number;
  isSnapped= false;

  ngOnInit() {

    this.faceSnap = new FaceSnap(
      'Shepard',
      'Normandy\'s Commander',
      'https://i.pinimg.com/564x/57/c1/71/57c171b8fd242ca1d6ced472250b3ef4.jpg',
      new Date(),
      0
    );

  }
  onAddSnap() {
    if (this.isSnapped===false){
       this.faceSnap.snaps++;
       this.isSnapped=true;
    }else{
      this.faceSnap.snaps--;
      this.isSnapped=false;
    }  } 


}
