import { Component, OnInit, Input } from '@angular/core';
import { faceSnap } from '../models/face-snap.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})

export class AppComponent implements OnInit {
@Input() mySnap!: faceSnap;

  ngOnInit() {
    this.mySnap = new faceSnap(
      'Shepard',
      'Normandy\'s Commander',
      'https://i.pinimg.com/564x/57/c1/71/57c171b8fd242ca1d6ced472250b3ef4.jpg',
      new Date(),
      0,
      false
    )

  }
  onAddSnap() {
    if (this.mySnap.isSnapped===false){
       this.mySnap.snaps++;
       this.mySnap.isSnapped=true;
    }else{
      this.mySnap.snaps--;
      this.mySnap.isSnapped=false;
    }  } 


}
