import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-face-snap',
  templateUrl: './face-snap.component.html',
  styleUrls: ['./face-snap.component.scss']
})
export class FaceSnapComponent implements OnInit {
  title!: string;
  description!: string;
  createdDate!: Date;
  snaps!: number;
  imageUrl!: string;
  isSnapped!:boolean;

  ngOnInit() {
    this.title='Shepard';
    this.description='Normandy\'s Commander';
    this.createdDate= new Date;
    this.snaps= 6;
    this.imageUrl = 'https://i.pinimg.com/564x/57/c1/71/57c171b8fd242ca1d6ced472250b3ef4.jpg';
    this.isSnapped=false;
  }

  onAddSnap() {
if (this.isSnapped=false){
   this.snaps++;
   this.isSnapped=true
}else

  } 
}
